import React from 'react';
import './Song.css';
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';
import { Container, Link, ButtonContainer } from "./SongElements"
import SpotifyPlayer from 'react-spotify-player'

class SongPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            uri: "",
            size: { width: '100%', height: '375' },
            view: 'list', // or 'coverart'
            theme: 'white' // or 'black'
        };
    }

    fetchPlaylist = () => {
        let playlist_uri = "";
        fetch("http://127.0.0.1:5000/getPlaylist")
            .then(response => response.json())
            .then(result => {
                playlist_uri = result
                console.log("RESULT FROM getPlaylist endpoint: " + result)
                this.setState({uri: result})
            })
            .catch(e => {
                console.log(e);
            });
        return playlist_uri
    };

    // const uri = "";
    // // List version with previews only
    // const size = {
    //     width: '100%',
    //     height: 375,
    //   };
    // const view = 'list'; // or 'coverart'
    // const theme = 'white'; // or 'black'

    render() {
        this.fetchPlaylist();
        return (
            <Container>
                <SpotifyPlayer
                    uri={this.fetchPlaylist}//"spotify:playlist:37i9dQZF1DXdQvOLqzNHSW"
                    size={this.state.size}
                    view={this.state.view}
                    theme={this.state.theme}
                />
                <br /><br />
                <ButtonContainer>
                    <Link to="/home" className="next_icon__Link"><ArrowForwardIcon /></Link>
                </ButtonContainer>
            </Container>
        );
    }
}
export default SongPage;
